import React, { Component } from "react";
import { Spring } from "react-spring/renderprops";
import { Container, Header } from "semantic-ui-react";

class Score extends Component {
  state = {
    percentage: 70
  };

  render() {
  //   return (
  //     <Container id="score">
  //       <Header>Score</Header>
  //         <div id="progress-div">
  //           <div id="progress-bar" role="progressbar" percentage={this.state.percentage} >
  //         </div>  
  //         </div>  
  //     </Container>
  //   );
  // }

  const progress = this.state.percentage

  return (
    <Spring from={{ percent: 0 }} to={{ percent: progress }}>
      {({ percent }) => (
        <div className="progress vertical">
          <div style={{ height: `${progress}%` }} className="progress-bar">
            <span className="sr-only">{`${progress}%`}</span>
          </div>
        </div>
      )}
    </Spring>
  );
}}

// ReactDOM.render(<ProgressBar percentage={you state for progress percentage} />, document.getElementById('bar'));

export default Score;
