import React from "react";
import { Button, Header, Icon, Modal } from "semantic-ui-react";
import { Redirect } from "react-router-dom";
import RegisterModal from "../components/RegisterModal.js";

class FormContainer extends React.Component {
  constructor() {
    super();
    this.state = {
      username: "",
      password: "",
      redirect: false
    };
  }

  loginChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  setRedirect = () => {
    this.setState({
      redirect: true
    });
  };

  handleSubmitFindUser = () => {
    fetch(`http://localhost:3000/users/${this.state.username}`)
      .then(resp => resp.json())
      .then(user => {
        if (user) {
          this.props.setUser(user);
          this.props.closeForm();
          this.setRedirect();
        } else {
          alert("incorrect username or password");
        }
      });
  };

  handleSubmitNewUser = () => {
    contentObj = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    };
    fetch(`http://localhost:3000/users`, contentObj)
      .then(resp => resp.json())
      .then(user => {
        if (user) {
          this.props.setUser(user);
          this.props.closeForm();
          this.setRedirect();
        } else {
          alert("passwords don't match");
        }
      });
  };

  render() {
    return this.state.redirect ? (
      <Redirect to="/play" />
    ) : (
      <Modal
        id="login-modal"
        open={this.props.hid}
        centered={false}
        onClose={this.props.toggleform}
      >
        <Header icon="browser" content="Login" />
        <Modal.Content>
          <div className="ui form">
            <div className="field">
              <label>Username</label>
              <input
                onChange={this.loginChange}
                type="text"
                name="username"
                placeholder="Username"
              ></input>
            </div>
            <div className="field">
              <label>Password</label>
              <input
                onChange={this.loginChange}
                name="password"
                type="password"
              ></input>
            </div>

            <div
              className="ui submit button"
              onClick={this.handleSubmitFindUser}
            >
              Play
            </div>
          </div>
        </Modal.Content>
        <Modal.Actions>
          <Button color="red" onClick={this.props.toggleForm}>
            Close
          </Button>
          <RegisterModal 
          loginChange
          setRedirect
          handleSubmitNewUser
          username
          password
          redirect
          />
        </Modal.Actions>
      </Modal>
    );
  }
}

export default FormContainer;
