import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom';
import React, { Component } from 'react'
import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

class NavBar extends Component {
  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })


  render() {
    const { children } = this.props
    const { fixed } = this.state
    return (
      <Container className="navbar">
        <Button><NavLink exact to="/">MapQuiz</NavLink></Button>
        {this.props.user ? (
          <React.Fragment>
            <Button><NavLink exact to="/play">Play</NavLink></Button>
            <Button><NavLink exact to="/stats">Stats</NavLink></Button>
            <Button style={{ color: "#009fda" }} onClick={this.props.handleLoginClick}>Logout</Button>
          </React.Fragment>
        ) : (
          <Button style={{ color: "#009fda" }} onClick={this.props.handleLoginClick}>Login</Button>
        )}
      </Container>
    );
  ;}
};



export default NavBar;
