import { NavLink } from "react-router-dom";
import React, { Component } from "react";
import { Button } from "semantic-ui-react";

class NavBar extends Component {
  state = {
    home: false,
    play: false,
    stats: false
  };

  toggleClass = () => {
    const currentHome = this.state.home;
    const currentPlay = this.state.play;
    const currentStats = this.state.stats;
    this.setState({
      home: !currentHome,
      play: !currentPlay,
      stats: !currentStats
    });
  };

  mapQuizButton = (
    <Button
      position={"left"}
      style={{ marginLeft: "0.5em" }}
      id="mapquiz-link"
      active
    >
      <NavLink id="mapquiz-text" exact to="/">
        <img id="logo" src={require("../favicon.ico")} alt="MapQuiz Logo"></img>
        MapQuiz
      </NavLink>
    </Button>
  );

  loginButton = (
    <Button
      className="nav-link"
      position={null}
      id="login-button"
      style={{ marginLeft: "0.5em" }}
      onClick={this.props.handleLoginClick}
    >
      Login
    </Button>
  );

  logoutButton = (
    <Button
      className="nav-link"
      style={{ marginLeft: "0.5em" }}
      id="logout-button"
      onClick={this.props.handleLogoutClick}
    >
      <NavLink exact to="/">
        Logout
      </NavLink>
    </Button>
  );

  playButton = (
    <NavLink exact to="/play">
      <Button
      className
        className={this.state.play ? "nav-link active" : "nav-link"}
        onClick={this.toggleClass}
        id="play-link"
      >
        Play
      </Button>
    </NavLink>
  );

  statsButton = (
    <Button
      className={this.state.stats ? "nav-link active" : "nav-link"}
      onClick={this.toggleClass}
      id="stats-link"
    >
      <NavLink exact to="/stats">
        Stats
      </NavLink>
    </Button>
  );

  render() {
    return (
      <div id="navbar">
        {this.mapQuizButton}
        {this.props.user ? null : this.loginButton}
        {this.props.user ? this.logoutButton : null}
        {this.statsButton}
        {this.props.user ? this.playButton : null}
      </div>
    );
  }
}

export default NavBar;
