import { NavLink } from "react-router-dom";
import React, { Component } from "react";
import { Button } from "semantic-ui-react";

class NavBar extends Component {
  state = {
    home: false,
    play: false,
    stats: false
  };

  toggleClass = () => {
    const currentHome = this.state.home;
    const currentPlay = this.state.play;
    const currentStats = this.state.stats;
    this.setState({
      home: !currentHome,
      play: !currentPlay,
      stats: !currentStats
    });
  };

  mapQuizButton = (
    <NavLink
      id="mapquiz-text"
      exact
      to="/"
      position={"left"}
      style={{ marginLeft: "0.5em" }}
      id="mapquiz-link"
    >
      <img id="logo" src={require("../favicon.ico")} alt="MapQuiz Logo"></img>
      MapQuiz
    </NavLink>
  );

  loginButton = (
    <NavLink
      className="nav-link nav-button"
      position={null}
      id="login-button"
      style={{ marginLeft: "0.5em" }}
      onClick={this.props.handleLoginClick}
    >
      Login
    </NavLink>
  );

  logoutButton = (
    <NavLink
      exact
      to="/"
      className="nav-link nav-button"
      style={{ marginLeft: "0.5em" }}
      id="logout-button"
      onClick={this.props.handleLogoutClick}
    >
      Logout
    </NavLink>
  );

  playButton = (
    <NavLink exact to="/play" className="nav-link">
      Play
    </NavLink>
  );

  statsButton = (
    <NavLink exact to="/stats" className="nav-link">
      Stats
    </NavLink>
  );

  render() {
    return (
      <div id="navbar">
        {this.mapQuizButton}
        {this.props.user ? null : this.loginButton}
        {this.props.user ? this.logoutButton : null}
        {this.statsButton}
        {this.props.user ? this.playButton : null}
      </div>
    );
  }
}

export default NavBar;
