import { NavLink } from "react-router-dom";
import React, { Component } from "react";
import { Button } from "semantic-ui-react";

class NavBar extends Component {
  constructor(props) {
    super(props);
    this.addActiveClass = this.addActiveClass.bind(this);
    this.state = {
      active: false
    };
  }
  toggleClass() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  }

  mapQuizButton = (
    <Button
      position={"left"}
      style={{ marginLeft: "0.5em" }}
      id="mapquiz-link"
      active
    >
      <NavLink id="mapquiz-text" exact to="/">
        <img id="logo" src={require("../favicon.ico")} alt="MapQuiz Logo"></img>
        MapQuiz
      </NavLink>
    </Button>
  );

  loginButton = (
    <Button
      className="nav-link"
      position={null}
      id="login-button"
      style={{ marginLeft: "0.5em" }}
      onClick={this.props.handleLoginClick}
    >
      Login
    </Button>
  );

  logoutButton = (
    <Button
      className="nav-link"
      style={{ marginLeft: "0.5em" }}
      id="logout-button"
      onClick={this.props.handleLogoutClick}
    >
      <NavLink exact to="/">
        Logout
      </NavLink>
    </Button>
  );

  playButton = (
    <Button
    className={this.state.active ? 'your_className': null} 
    onClick={this.toggleClass} 
      id="play-link"
    >
      <NavLink exact to="/play">
        Play
      </NavLink>
    </Button>
  );

  statsButton = (
    <Button
      onClick={this.toggleClass}
      // className={this.state.active ? "nav-link active" : "nav-link"}
      id="stats-link"
    >
      <NavLink exact to="/stats">
        Stats
      </NavLink>
    </Button>
  );

  render() {
    return (
      <div id="navbar">
        {this.mapQuizButton}
        {this.props.user ? null : this.loginButton}
        {this.props.user ? this.logoutButton : null}
        {this.statsButton}
        {this.props.user ? this.playButton : null}
      </div>
    );
  }
}

export default NavBar;
