import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom';
import React, { Component } from 'react'
import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}

const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      content='Imagine-a-Company'
      inverted
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
      }}
    />
    <Header
      as='h2'
      content='Do whatever you want when you want to.'
      inverted
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
      }}
    />
    <Button primary size='huge'>
      Get Started
      <Icon name='right arrow' />
    </Button>
  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

class NavBar extends Component {
  hideFixedMenu = () => this.setState({ fixed: false })
  showFixedMenu = () => this.setState({ fixed: true })


  render() {
    const { children } = this.props
    const { fixed } = this.state
//     return (
//       <Container className="navbar">
//         <Button><NavLink exact to="/">MapQuiz</NavLink></Button>
//         {this.props.user ? (
//           <React.Fragment>
//             <Button><NavLink exact to="/play">Play</NavLink></Button>
//             <Button><NavLink exact to="/stats">Stats</NavLink></Button>
//             <Button style={{ color: "#009fda" }} onClick={this.props.handleLoginClick}>Logout</Button>
//           </React.Fragment>
//         ) : (
//           <Button style={{ color: "#009fda" }} onClick={this.props.handleLoginClick}>Login</Button>
//         )}
//       </Container>
//     );
//   ;}
// };

return (
  <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
    <Visibility
      once={false}
      onBottomPassed={this.showFixedMenu}
      onBottomPassedReverse={this.hideFixedMenu}
    >
      <Segment
        inverted
        textAlign='center'
        style={{ minHeight: 700, padding: '1em 0em' }}
        vertical
      >
        <Menu
          fixed={fixed ? 'top' : null}
          inverted={!fixed}
          pointing={!fixed}
          secondary={!fixed}
          size='large'
        >
          <Container>
            <Menu.Item as='a' active>
              MapQuiz
            </Menu.Item>
            <Menu.Item as='a'>Work</Menu.Item>
            <Menu.Item as='a'>Company</Menu.Item>
            <Menu.Item as='a'>Careers</Menu.Item>
            <Menu.Item position='right'>
              <Button as='a' inverted={!fixed}>
                Log in
              </Button>
              <Button as='a' inverted={!fixed} primary={fixed} style={{ marginLeft: '0.5em' }}>
                Sign Up
              </Button>
            </Menu.Item>
          </Container>
        </Menu>
        <HomepageHeading />
      </Segment>
    </Visibility>

    {children}
  </Responsive>
)
}
}

DesktopContainer.propTypes = {
children: PropTypes.node,
}

export default NavBar;
