import React from "react";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker
} from "react-google-maps";
import MapOptions from "../MapOptions.json";

let arr = [
  { lat: 48.8667, lng: 2.3333 },
  { lat: 55.7522, lng: 37.6155 },
  { lat: -22.925, lng: -43.225 }
];

let handleClick = (e, props) => {
  props.toggleMarker();
  let latLng = e.latLng;
  props.setChoice(latLng);
  let mylatlng = new window.google.maps.LatLng({
    lat: props.currentCity.lat,
    lng: props.currentCity.long
  });
  let distance =
    window.google.maps.geometry.spherical.computeDistanceBetween(
      latLng,
      mylatlng
    ) / 10000;
  console.log(distance);
  if (props.currentScore - distance >= 0) {
    props.setScore(distance);
    props.toggleNextButton();
    let formattedQuestion = {
      distance: distance,
      city_id: props.currentCity.id
    };
    props.addQuestion(formattedQuestion);

    if (props.cityIndex >= 19) {
      props.createGame();
      props.showScoreModal();
    }
  } else {
    props.showModal();
  }
};

let formatlatlng = currentCity => {
  return { lat: currentCity.lat, lng: currentCity.long };
};

const showCityMarker = props => {
  // setTimeout(() => {
  return props.isMarkerShown && props.currentCity ? (
    <Marker
      icon={{
        url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png"
      }}
      position={formatlatlng(props.currentCity)}
    />
  ) : null;
  // }, 1000)
};

const Map = withScriptjs(
  withGoogleMap(props => (
    <React.Fragment>
      {console.log(props)}
      <GoogleMap
        defaultZoom={2.3}
        defaultCenter={{ lat: 25, lng: 0 }}
        defaultOptions={{
          styles: MapOptions,
          maxZoom: 7,
          minZoom: 2.3,
          gestureHandling: "greedy"
        }}
        onClick={e => handleClick(e, props)}
      >
        {props.yourChoice ? (
          <Marker
            icon={{
              url: "http://maps.google.com/mapfiles/ms/icons/blue-dot.png"
            }}
            position={props.yourChoice}
          />
        ) : null}
        {showCityMarker(props)}
      </GoogleMap>
    </React.Fragment>
  ))
);

export default Map;
