import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom';
import React, { Component } from 'react'
import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}

class NavBar extends Component {
  render() {
    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
          <Segment id
            textAlign='center'
            style={{ minHeight: 70, padding: '1em 0em' }}
            vertical
          >
            <Menu size='large' className="ui menu" background="#f7f7f7">
              <Container>
                <Menu.Item as='a' active><NavLink exact to="/">MapQuiz</NavLink></Menu.Item>
                {this.props.user ? (
                  <React.Fragment>
                    <Menu.Item as='a'><NavLink exact to="/play">Play</NavLink></Menu.Item>
                    <Menu.Item as='a'><NavLink exact to="/stats">Stats</NavLink></Menu.Item>
                    <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Logout</Button>
                  </React.Fragment>
                ) : (
                  <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Login</Button>
                )}
              </Container>
            </Menu>
          </Segment>
      </Responsive>
    )       
  }
}

export default NavBar;
