import React, { Component } from "react";
import { Spring } from "react-spring/renderprops";
import { Container, Header } from "semantic-ui-react";

class Score extends Component {
  state = {previousScore: 0}

  percentage = this.props.score/20

  setScore = () => {
    this.setState(previousScore)
  }

  render() {
    return (
      <Container id="score">
        <Header>Score</Header>
        <Spring from={{ percent: 0 }} to={{ percent: this.percentage }}>
          {({ percent }) => (
            <div className="progress vertical">
              <div
                style={{ height: `${this.percentage}%` }}
                className="progress-bar"
              >
                <span className="sr-only">{`${Math.round(this.props.score)}`}</span>
              </div>
            </div>
          )}
        </Spring>
      </Container>
    );
  }
}

export default Score;
