import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom';
import React, { Component } from 'react'
import {
  Button,
  Menu,
  Responsive
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}

class NavBar extends Component {
  render() {
    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Menu id="navbar" size='large'>
            <Menu.Item as='a' active><NavLink exact to="/">MapQuiz</NavLink></Menu.Item>
            {this.props.user ? (
              <React.Fragment>
                <Menu.Item id="plah-button" as='a'><NavLink exact to="/play">Play</NavLink></Menu.Item>
                <Menu.Item id="plah-button" as='a'><NavLink exact to="/stats">Stats</NavLink></Menu.Item>
                <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Logout</Button>
              </React.Fragment>
            ) : (
              <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Login</Button>
            )}
        </Menu>
      </Responsive>
    )       
  }
}

export default NavBar;
