import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom';
import React, { Component } from 'react'
import {
  Button,
  Container,
  Divider,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Responsive,
  Segment,
  Sidebar,
  Visibility,
} from 'semantic-ui-react'

const getWidth = () => {
  const isSSR = typeof window === 'undefined'
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
}

const HomepageHeading = ({ mobile }) => (
  <Container text>
    <Header
      as='h1'
      content='Imagine-a-Company'
      inverted
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'normal',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
      }}
    />
    <Header
      as='h2'
      content='Do whatever you want when you want to.'
      inverted
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
      }}
    />
    <Button primary size='huge'>
      Get Started
      <Icon name='right arrow' />
    </Button>
  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

class NavBar extends Component {
  render() {




return (
  <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
      <Segment
        inverted
        textAlign='center'
        style={{ minHeight: 700, padding: '1em 0em' }}
        vertical
      >
        <Menu
          size='large'>
          <Container>
            <Menu.Item as='a' active><NavLink exact to="/">MapQuiz</NavLink></Menu.Item>
            {this.props.user ? (
          <React.Fragment>
            <Menu.Item as='a'><NavLink exact to="/play">Play</NavLink></Menu.Item>
            <Menu.Item as='a'><NavLink exact to="/stats">Stats</NavLink></Menu.Item>
            <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Logout</Button>
            </React.Fragment>
            ) : (
            <Button as='a' style={{ marginLeft: '0.5em' }} onClick={this.props.handleLoginClick}>Login</Button>
          )}

          </Container>
        </Menu>
        <HomepageHeading />
      </Segment>
  </Responsive>
)
}
}

NavBar.propTypes = {
children: PropTypes.node,
}

export default NavBar;
